mapper.select:
select max(channel_type_id) from physical_channel_binding where physical_id=#{physical_id}
and channel_code in

    #{item}

mapper.update:
update physical_channel_binding set channel_type_id=#{id} where physical_id=#{physical_id}
and channel_code in

    #{item}

update physical_channel_binding set collect_id=#{collect_id} where  physical_id=#{physical_id}
and channel_code in

    #{item}

mapper.select:
select id,`type`,frequency,duration,accuracy,`interval`,`name`,m.enterprise_id,
(select enterprise_name from enterprise_info where enterprise_id=m.enterprise_id) as enterprise_name
  from channel_parameter m where 1=1

    and id=#{id} 


    and `name` like concat('%',#{name},'%')


    and enterprise_id=#{enterprise_id}
