mapper.select:
select id,user_name,user_password,user_real_name,user_phone,user_status,u.enterprise_id,
(select enterprise_name from enterprise_info where enterprise_id=u.enterprise_id) enterprise_name,
register_time,update_time,role_id,
(select role_name from role where id=role_id) as role_name
  from `user` u where 1=1

    and id=#{id}


    and user_name=#{user_name}


    and enterprise_id=#{enterprise_id}


    and role_id=#{role_id}
